  \node  (0) {$\mathsf {F}^{\mathbb {L}}\coprod _{I}{\mathsf {L}\mathsf {U}^{\mathbb {L}}X_{\bullet }}$};
  \node [right = 4.5cm of 0] (1) {$\mathsf {F}^{\mathbb {L}}\mathsf {L}\coprod _{I}{\mathsf {U}^{\mathbb {L}}X_{\bullet }}$};
