\begin {array}{l}
  \mathsf {INatStr}[u] :\equiv 
  \mathchoice {\textstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}
  \mathchoice {\textstyle \prod _{{\mathopen {}\left (f : X\multimap  Y\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left (f : X\multimap  Y\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left (f : X\multimap  Y\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left (f : X\multimap  Y\right )\mathclose {}}}}
  {f {\mathopen {}\left (uX\right )\mathclose {}}} =_{Y} {uY}
  \\
  \bar {\eta }_{\mathsf {INatStr}}\, a\, f :\equiv  \eta _{f}a
\end {array}

