  \begin {array}{l}
    \mathchoice {\textstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left ({\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}\right )\mathclose {}}}}
