    \mathchoice {\textstyle \prod _{{\mathopen {}\left (g : Y\multimap  Z\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left (g : Y\multimap  Z\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left (g : Y\multimap  Z\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left (g : Y\multimap  Z\right )\mathclose {}}}}
    \mathchoice {\textstyle \prod _{{\mathopen {}\left (h : X \multimap  Z\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left (h : X \multimap  Z\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left (h : X \multimap  Z\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left (h : X \multimap  Z\right )\mathclose {}}}}
    \mathchoice {\textstyle \prod _{{\mathopen {}\left (\phi  : {f;g} \sim _{X\multimap  Z} {h}\right )\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left (\phi  : {f;g} \sim _{X\multimap  Z} {h}\right )\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left (\phi  : {f;g} \sim _{X\multimap  Z} {h}\right )\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left (\phi  : {f;g} \sim _{X\multimap  Z} {h}\right )\mathclose {}}}} \\
    \mathsf {ap}_{g} {\mathopen {}\left (\vartheta _{u} f\right )\mathclose {}} \bullet  \vartheta _{u} g
    = \phi {\mathopen {}\left (\epsilon _{u} X\right )\mathclose {}} \bullet  \vartheta _{u} h
  \end {array}
\end {array}

  \mathchoice {\textstyle \prod _{{\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}}}{\textstyle \prod _{{\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}}}{\scriptstyle \prod _{{\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}}}{\scriptscriptstyle \prod _{{\mathopen {}\left \{X,Y,Z:{A}\downarrow {\mathcal {U}}\right \}\mathclose {}}}}
