\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    height = 1.5cm,
    west/style = lies over,
    east/style = lies over,
    nw/style = pullback,
    sw = y,
    nw = \bar {y},
    se = z,
    ne = \bar {z},
    south = g,
    north = \bar {g},
  }
  \node  (x*) [dotted pullback, left = of nw] {$\bar {x}$};
  \node  (x) [left = of sw] {$x$};
  \draw [->] (x*) to node [above] {$\bar {f}$} (nw);
  \draw [lies over] (x*) to (x);
  \draw [->] (x) to node [below] {$f$} (sw);
\end {tikzpicture}


