  between/.style args={#1 and #2}{
    at = ($(#1)!0.5!(#2)$)
  },
  diagram/.style = {
    on grid,
    node distance=2cm,
    commutative diagrams/every diagram,
    line width = .5pt,
    every node/.append style = {
      commutative diagrams/every cell,
    }
  },
  fibration/.style = {
    -{Triangle[open]}
  },
  etale/.style = {
    -{Triangle[open]}
  },
  etale cover/.style= {
    >={Triangle[open]},->.>
  },
  opfibration/.style = {
    -{Triangle}
  },
  lies over/.style = {
    |-{Triangle[open]}
  },
  op lies over/.style = {
    |-{Triangle}
  },
  embedding/.style = {
    {right hook}->
  },
  open immersion/.style = {
    {right hook}-{Triangle[open]}
  },
  closed immersion/.style = {
    {right hook}-{Triangle}
  },
  closed immersion*/.style = {
    {left hook}-{Triangle}
  },
  embedding*/.style = {
    {left hook}->
  },
  open immersion*/.style = {
    {left hook}-{Triangle[open]}
  },
  exists/.style = {
    densely dashed
  },
}

