  \SpliceDiagramSquare {
    height = 1.5cm,
    nw/style = pullback,
    west/style = lies over,
    east/style = lies over,
    north = \bar {y}_{f},
    south = f,
    nw = \bar {y}_{x},
    ne = \bar {y},
    sw = x,
    se = y
  }
  \node  (x*) [left = of nw] {$\bar {x}$};
  \node  (x) [left = of sw] {$x$};
  \draw [->] (nw) to node [above] {$h$} (x*);
  \draw [lies over] (x*) to (x);
  \draw [double] (x) to (sw);
\end {tikzpicture}


