    north = {\__jon_tikz_diagram_fmt_placeholder:n {north}},
    south = {\__jon_tikz_diagram_fmt_placeholder:n {south}},
    west = {\__jon_tikz_diagram_fmt_placeholder:n {west}},
    east = {\__jon_tikz_diagram_fmt_placeholder:n {east}},
  }
