\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    nw = A,
    sw = A,
    ne = \mathbf {1}_{},
    se = D,
    north = !_{A},
    east = d_\bot ,
    south = d_A,
    west/style = double,
  }
  \node  [between = nw and se] {$\sqsupseteq $};
\end {tikzpicture}


