    -{Triangle}
  },
  lies over/.style = {
    |-{Triangle[open]}
  },
  op lies over/.style = {
    |-{Triangle}
  },
  embedding/.style = {
    {right hook}->
  },
  open immersion/.style = {
    {right hook}-{Triangle[open]}
  },
  closed immersion/.style = {
    {right hook}-{Triangle}
  },
  closed immersion*/.style = {
    {left hook}-{Triangle}
  },
  embedding*/.style = {
    {left hook}->
  },
  open immersion*/.style = {
    {left hook}-{Triangle[open]}
  },
  exists/.style = {
    densely dashed
  },
}

