\NewDocumentCommand\CreateRect{D<>{} m m}{
  \path
    coordinate (#1nw)
    ++(#2,-#3) coordinate (#1se)
    coordinate (#1sw) at (#1se -| #1nw)
    coordinate (#1ne) at (#1nw -| #1se)
  ;

  \path[spath/save = #1north] (#1nw) to (#1ne);
  \path[spath/save = #1west] (#1nw) to (#1sw);
  \path[spath/save = #1east] (#1ne) to (#1se);
  \path[spath/save = #1south] (#1sw) to (#1se);
}


 \usepackage{tikz, tikz-cd, mathtools, amssymb, stmaryrd}
\usetikzlibrary{matrix,arrows}
\usetikzlibrary{backgrounds,fit,positioning,calc,shapes}
\usetikzlibrary{decorations.pathreplacing}
\usetikzlibrary{decorations.pathmorphing}
\usetikzlibrary{decorations.markings}

\tikzset{
  desc/.style={sloped, fill=white,inner sep=2pt},
  upright desc/.style={fill=white,inner sep=2pt},
  pullback/.style = {
    append after command={
      \pgfextra{
        \draw ($(\tikzlastnode) + (.2cm,-.5cm)$) -- ++(0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  pullback 45/.style = {
    append after command={
      \pgfextra{
        \draw[rotate = 45] ($(\tikzlastnode) + (.2cm,-.5cm)$) -- ++(0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  ne pullback/.style = {
    append after command={
      \pgfextra{
        \draw ($(\tikzlastnode) + (-.2cm,-.5cm)$) -- ++(-0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  sw pullback/.style = {
    append after command={
      \pgfextra{
        \draw ($(\tikzlastnode) + (.2cm,.5cm)$) -- ++(0.3cm,0) -- ++(0,-0.3cm);
      }
    }
  },
  dotted pullback/.style = {
    append after command={
      \pgfextra{
        \draw [densely dotted] ($(\tikzlastnode) + (.2cm,-.5cm)$) -- ++(0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  muted pullback/.style = {
    append after command={
      \pgfextra{
        \draw [gray] ($(\tikzlastnode) + (.2cm,-.5cm)$) -- ++(0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  pushout/.style = {
    append after command={
      \pgfextra{
        \draw ($(\tikzlastnode) + (-.2cm,.5cm)$) -- ++(-0.3cm,0) -- ++(0,-0.3cm);
      }
    }
  },
  between/.style args={#1 and #2}{
    at = ($(#1)!0.5!(#2)$)
  },
  diagram/.style = {
    on grid,
    node distance=2cm,
    commutative diagrams/every diagram,
    line width = .5pt,
    every node/.append style = {
      commutative diagrams/every cell,
    }
  },
  fibration/.style = {
    -{Triangle[open]}
  },
  etale/.style = {
    -{Triangle[open]}
  },
  etale cover/.style= {
    >={Triangle[open]},->.>
  },
  opfibration/.style = {
    -{Triangle}
  },
  lies over/.style = {
    |-{Triangle[open]}
  },
  op lies over/.style = {
    |-{Triangle}
  },
  embedding/.style = {
    {right hook}->
  },
  open immersion/.style = {
    {right hook}-{Triangle[open]}
  },
  closed immersion/.style = {
    {right hook}-{Triangle}
  },
  closed immersion*/.style = {
    {left hook}-{Triangle}
  },
  embedding*/.style = {
    {left hook}->
  },
  open immersion*/.style = {
    {left hook}-{Triangle[open]}
  },
  exists/.style = {
    densely dashed
  },
}

\newlength{\dontworryaboutit}

\tikzset{
  inline diagram/.style = {
    commutative diagrams/every diagram,
    commutative diagrams/cramped,
    line width = .5pt,
    every node/.append style = {
      commutative diagrams/every cell,
      anchor = base,
      inner sep = 0pt
    },
    every path/.append style = {
      outer xsep = 2pt
    }
  }
}

