\begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
  \path  coordinate (nw)
    ++ (1,0) coordinate[label=above:$X$] (n/X)
    ++ (3,-3) coordinate[label=below:$Y$] (s/Y)
    ++ (1,0) coordinate (se);
  \draw [spath/save=swoosh] (n/X) to[out=-90,in=90] (s/Y);
  \path 
    coordinate[dot,label=below:$\alpha _{X}$] (alg) at (spath cs:swoosh 0.4)
    coordinate[dot,label=above:$h$] (h) at (spath cs:swoosh 0.7)
    coordinate[label=above:$F$] (n/F) at (nw -| alg);
  \draw  (n/F) to (alg.center);
  \begin {scope}[on background layer]
    \fill [catc] (n/X) to[spath/use={swoosh,weld}] (s/Y) to (se) to (nw -| se) to cycle;
  \end {scope}
\end {tikzpicture}
