\begin {tikzpicture}[scale=0.5]
  \path 
    coordinate (nw)
    ++(1,0) coordinate[label=above:$X$] (n/X)
    ++(3,-3) coordinate[label=below:$X$] (s/X)
    ++(1,0) coordinate (se)
  ;
