  \path 
    coordinate[dot,label=below:$x$] (suc) at (spath cs:swoosh 0.33)
    coordinate[dot,label=below:$\alpha _{X}$] (alg) at (spath cs:swoosh 0.6)
    coordinate[label=above:$F$] (n/F) at (nw -| alg)
  ;
  \draw  (n/F) to (alg.center);
  \begin {scope}[on background layer]
    \fill [catc] (n/X) to[spath/use={swoosh,weld}] (s/X) to (se) to (nw -| se) to cycle;
  \end {scope}
\end {tikzpicture}


 \RequirePackage{tikz}
\RequirePackage{amsmath}
