    ++(1,0) coordinate[label=above:$X$] (t)
    ++(0,-3) coordinate[label=below:$X$] (b)
    ++(1,0) coordinate (br);
  \draw  (t) to (b);
  \begin {scope}[on background layer]
    \fill [catc] (t) to (b) to (br) to (t -| br) to cycle;
  \end {scope}
\end {tikzpicture}


