    ++(1,-4) coordinate[label=below:$X$] (b1)
    ++(1,0) coordinate (b2);
  \draw [spath/save=swoosh] (t1) to[out=-90,in=90] (b1);
  \path [spath/save=vert1] (t2) to (b2 -| t2);
  \path [spath/save=vert2] (t3) to (b2 -| t3);
  \path [name intersections={of=swoosh and vert1}] coordinate[dot, label={[yshift=1ex]-100:$\alpha _X$}] (alg1) at (intersection-1);
  \path [name intersections={of=swoosh and vert2}] coordinate[dot, label={[yshift=1ex]-100:$\alpha _X$}] (alg2) at (intersection-1);
  \draw  (t2) to (alg1);
  \draw  (t3) to (alg2);
  \begin {scope}[on background layer]
    \fill [catc] (t1) to[spath/use={swoosh,weld}] (b1) to (b2) to (t1 -| b2) to cycle;
  \end {scope}
\end {tikzpicture}

fr:mainmatter.html:p:
Let \mathbb {T} be a monad on a category \mathcal {C}, and let X be a weak algebra for \mathbb {T} that is a fixed point, i.e. the algebra map is invertible by {X}\xrightarrow {{\pi _{X}}}{TX}. We may define the induced \mathbb {T}-algebra \bar {X} to have the same carrier object as X and with the algebra map {TX}\xrightarrow {{\alpha _{\bar {X}}}}{X} defined as follows:






  \RequirePackage{tikz}
 \RequirePackage{amsmath}
 \usetikzlibrary{backgrounds, intersections, calc, spath3, fit}

 \definecolor{catccolor}{RGB}{255,244,138}
 \tikzstyle{dot}=[circle, draw=black, fill=black, minimum size=1mm, inner sep=0mm]
 \tikzstyle{catc}=[catccolor!60]
 \tikzstyle{catd}=[orange!40]
 \tikzstyle{cate}=[red!40]
 \tikzstyle{catf}=[blue!10]
 \tikzstyle{catg}=[green!25]


 \tikzstyle{blue halo}=[fill=blue!10, opacity=0.7, rounded corners]
 \tikzstyle{white halo}=[fill=white, opacity=0.7, rounded corners]

 \NewDocumentCommand\CreateRect{D<>{} m m}{
   \path
     coordinate (#1nw)
     ++(#2,-#3) coordinate (#1se)
     coordinate (#1sw) at (#1se -| #1nw)
     coordinate (#1ne) at (#1nw -| #1se)
   ;

   \path[spath/save = #1north] (#1nw) to (#1ne);
   \path[spath/save = #1west] (#1nw) to (#1sw);
   \path[spath/save = #1east] (#1ne) to (#1se);
   \path[spath/save = #1south] (#1sw) to (#1se);
 }


