  \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
    \path 
      coordinate (nw)
      ++(1,0) coordinate[label=above:$I$] (n/I)
      ++(3,-3) coordinate[label=below:$X$] (s/X)
      ++(1,0) coordinate (se);
    \draw [spath/save=wire] (n/I) to[out=-90,in=90] (s/X);
    \path 
      coordinate[dot,label=-135:$\pi _{I}$] (pi) at (spath cs:wire 0.1)
      coordinate[dot,label=below:$\alpha _{I}$] (alg) at (spath cs:wire 0.5)
      coordinate[dot,label=10:$j$] (inj) at (spath cs:wire 0.75)
      (alg) ++(0,.6) coordinate[dot,label=right:$\mu $] (mul)
      coordinate[label=above:$T$] (n/T) at (nw -| alg)
    ;
    \draw  (pi.center) to (mul.center);
    \draw  (mul.center) to (alg.center);
    \draw  (n/T) to (mul.center);
    \begin {scope}[on background layer]
      \fill [catc] (n/I) to[spath/use={wire,weld}] (s/X) to (se) to (nw -| se) to cycle;

          \node [blue halo,fit=(alg)(inj)]{};

    \end {scope}
  \end {tikzpicture}

        $=$

  \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
    \path 
      coordinate (nw)
      ++(1,0) coordinate[label=above:$I$] (n/I)
      ++(3,-3) coordinate[label=below:$X$] (s/X)
      ++(2,0) coordinate (se);
    \draw [spath/save=wire] (n/I) to[out=-90,in=90] (s/X);
    \path 
      coordinate[dot,label=-135:$\pi _{I}$] (pi) at (spath cs:wire 0.1)
      coordinate[dot,label=right:$\alpha _{x\cdot X}$] (alg) at (spath cs:wire 0.75)
      coordinate[dot,label=below:$j$] (inj) at (spath cs:wire 0.5)
      (alg) ++(0,1) coordinate[dot,label=right:$\mu $] (mul)
      coordinate[label=above:$T$] (n/T) at (nw -| alg)
