      \path 
        coordinate (nw)
        ++(1,0) coordinate[label=above:$X$] (n/X)
        ++(5,-4) coordinate[label=below:$X$] (s/X)
        ++(1,0) coordinate (se);
      \draw [spath/save=wire] (n/X) to[out=-90,in=90] (s/X);
      \path 
        coordinate[dot,label=left:$\pi _{X}$] (pi) at (spath cs:wire 0.15)
        coordinate[dot,label=-135:$\alpha _{X}$] (alg) at (spath cs:wire 0.8)
        coordinate[label=above:$T$] (n/T) at (nw -| alg)
      ;
      \draw [spath/save=vert] (alg) to (n/T);
      \path  coordinate[dot,label=right:$\mu $] (mu) at (spath cs:vert 0.4);
      \draw  (pi.center) to (mu.center);
      \begin {scope}[on background layer]
        \fill [catc] (n/X) to[spath/use={wire,weld}] (s/X) to (se) to (nw -| se) to cycle;
      \end {scope}
    \end {tikzpicture}

