    north/node/style = upright desc,
    west/node/style = upright desc,
  }
  \node [above left = of sq/nw,xshift=-.5cm] (nw) {$\Delta .B$};
  \node [below = of nw] (sw) {$\Delta $};
  \draw [->] (nw) to node[left] {$p_B$} (sw);
  \draw [->] (sw) to node[sloped,below] {$\gamma $} (sq/sw);
  \draw [->,bend left=30] (nw) to node[sloped,above] {$a$} (sq/ne);
  \draw [->,exists] (nw) to node[desc] {$\gamma .a$} (sq/nw);
\end {tikzpicture}


