  \SpliceDiagramSquare <sq/>{
    nw = \Gamma .A,
    sw = \Gamma ,
    ne = E,
    se = U,
    east = p,
    south = A,
    west = p_A,
    north = q_A,
    nw/style = pullback,
    north/node/style = upright desc,
    west/node/style = upright desc,
  }
  \node [above left = of sq/nw,xshift=-.5cm] (nw) {$\Delta $};
  \draw [->,bend right=30] (nw) to node[left] {$\gamma $} (sq/sw);
  \draw [->,bend left=30] (nw) to node[sloped,above] {$a$} (sq/ne);
  \draw [->,exists] (nw) to node[desc] {${\mathopen {}\left \langle \gamma ,a\right \rangle \mathclose {}}$} (sq/nw);
\end {tikzpicture}


