  \CreateRect {2}{3}
  \path 
    coordinate[label=above:$q$] (n/q) at (spath cs:north 0.5)
    coordinate[label=below:$q$] (s/q) at (spath cs:south 0.5)
  ;
  \draw  (n/q) to (s/q);
  \begin {scope}[on background layer]
    \fill [catc] (nw) rectangle (s/q);
    \fill [catd] (n/q) rectangle (se);
  \end {scope}
\end {tikzpicture}


