    nw = {\__jon_tikz_diagram_fmt_placeholder:n {nw}},
    sw = {\__jon_tikz_diagram_fmt_placeholder:n {sw}},
    se = {\__jon_tikz_diagram_fmt_placeholder:n {se}},
