      \path [name intersections={of=f01 and lBA}]
        coordinate[dot,
    label=-10:$\Phi _{c}$
  ] (lambda) at (intersection-1)
      ;

      \begin {scope}[on background layer]
        \fill [catc] (nw) rectangle (se);
        \tikzset {
          spath/split at intersections={f01}{lBA},
