  square/nw/.style = {},
  square/ne/.style = {},
  square/se/.style = {},
  square/sw/.style = {},
  square/north/.style = {->},
  square/south/.style = {->},
  square/west/.style = {->},
  square/east/.style = {->},
  square/north/node/.style = {above},
  square/south/node/.style = {below},
  square/west/node/.style = {left},
  square/east/node/.style = {right},
}

