          coordinate[label=above:$\strut p$] (n/p) at (spath cs:north 0.33)
          coordinate[label=below:$\strut p$] (s/p) at (spath cs:south 0.33)
          coordinate[label=above:$\strut c$] (n/c) at (spath cs:north 0.66)
          coordinate[label=below:$\strut c$] (s/c) at (spath cs:south 0.66)
        ;

        \node [between = nw and s/p, opacity=0.6] {$E$};
        \node [between = n/p and s/c, opacity=0.6] {$U$};
        \node [between = n/c and se, opacity=0.6] {$\mathcal {C}$};

        \draw  (n/p) to (s/p);
        \draw  (n/c) to (s/c);

