      \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
        \CreateRect {3}{2}
        \path 
          coordinate[label=above:$\strut q$] (n) at (spath cs:north 0.5)
          coordinate[label=below:$\strut q$] (s) at (spath cs:south 0.5)
        ;
        \draw  (n) to (s);
        \node [between = nw and s, opacity=0.6] {$U$};
        \node [between = n and se, opacity=0.6] {$E$};
        \begin {scope}[on background layer]
          \fill [catc] (nw) rectangle (s);
          \fill [catd] (n) rectangle (se);
        \end {scope}
      \end {tikzpicture}


          \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$:)]
            \CreateRect {3}{2}
            \path 
              coordinate[label=below:$\strut p$] (L) at (spath cs:south 0.25)
              coordinate[label=below:$\strut q$] (R) at (spath cs:south 0.75)
              coordinate[between = L and R] (LR)
            ;
            \path [spath/save=guide] (LR) to (nw -| LR);
            \draw [spath/save=cap] (L) to[out=90,in=90,looseness=2.5] (R);
            \path [name intersections={of=guide and cap}]
              coordinate[dot,label=below:$\eta $] (unit) at (intersection-1)
            ;
            \begin {scope}[on background layer]
              \fill [catd] (sw) to (L) to[spath/use={cap,weld}] (R) to (se) to (ne) to (nw) to cycle;
              \fill [catc] (L) to[spath/use={cap,weld}] (R) to cycle;
            \end {scope}
          \end {tikzpicture}


          \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
            \CreateRect {3}{2}
            \path 
              coordinate[label=above:$\strut {p}$] (L) at (spath cs:north 0.75)
              coordinate[label=above:$\strut {q}$] (R) at (spath cs:north 0.25)
              coordinate[between = L and R] (LR)
            ;
            \path [spath/save=guide] (LR) to (sw -| LR);
            \draw [spath/save=cup] (R) to[out=-90,in=-90,looseness=2.5] (L);
            \path [name intersections={of=guide and cup}]
              coordinate[dot,label=above:$\epsilon $] (counit) at (intersection-1)
            ;
            \begin {scope}[on background layer]
              \fill [catc] (nw) to (R) to[spath/use={cup,weld}] (L) to (ne) to (se) to (sw) to cycle;
              \fill [catd] (R) to [spath/use={cup,weld}] (L) to cycle;
            \end {scope}
          \end {tikzpicture}










