 \keys_set:nn { jon-tikz/diagram } {
   glue~target = {},
 }


 \cs_new:Nn \__jon_tikz_render_square:nn {
   \group_begin:
   \keys_set:nn {jon-tikz/diagram} {#2}
   \bool_if:nTF \l_jon_glue_west {
     \node (#1ne) [right = \l_jon_tikz_diagram_width~of~\l_jon_tikz_glue_target ne,square/ne] {$\l_jon_tikz_diagram_ne$};
     \node (#1se) [below = \l_jon_tikz_diagram_height~of~#1ne,square/se] {$\l_jon_tikz_diagram_se$};
     \draw[square/north] (\l_jon_tikz_glue_target ne) to node [square/north/node] {$\l_jon_tikz_diagram_north$} (#1ne);
     \draw[square/east] (#1ne) to node [square/east/node] {$\l_jon_tikz_diagram_east$} (#1se);
     \draw[square/south] (\l_jon_tikz_glue_target se) to node [square/south/node] {$\l_jon_tikz_diagram_south$} (#1se);
   } {
     \node (#1nw) [square/nw] {$\l_jon_tikz_diagram_nw$};
     \node (#1sw) [below = \l_jon_tikz_diagram_height~of~#1nw,square/sw] {$\l_jon_tikz_diagram_sw$};
     \draw[square/west] (#1nw) to node [square/west/node] {$\l_jon_tikz_diagram_west$} (#1sw);

     \node (#1ne) [right = \l_jon_tikz_diagram_width~of~#1nw,square/ne] {$\l_jon_tikz_diagram_ne$};
     \node (#1se) [below = \l_jon_tikz_diagram_height~of~#1ne,square/se] {$\l_jon_tikz_diagram_se$};
     \draw[square/north] (#1nw) to node [square/north/node] {$\l_jon_tikz_diagram_north$} (#1ne);
     \draw[square/east] (#1ne) to node [square/east/node] {$\l_jon_tikz_diagram_east$} (#1se);
     \draw[square/south] (#1sw) to node [square/south/node] {$\l_jon_tikz_diagram_south$} (#1se);
   }
   \group_end:
 }

 \NewDocumentCommand\SpliceDiagramSquare{D<>{}m}{
   \__jon_tikz_render_square:nn {#1} {#2}
 }


 \NewDocumentCommand\DiagramSquare{D<>{}O{}m}{
   \begin{tikzpicture}[diagram,#2,baseline=(#1sw.base)]
     \__jon_tikz_render_square:nn {#1} {#3}
   \end{tikzpicture}
 }

 \ExplSyntaxOff




          \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
              \CreateRect {3}{3}
              \path 
                coordinate[label=above:$\strut p$] (n/L) at (spath cs:north 0.8)
                coordinate[label=below:$\strut q$] (s/L) at (spath cs:south 0.2)
              ;
              \path [spath/save=guide1] (n/L) to[out=180,in=90] (s/L);
              \path [spath/save=guide2] (s/L) to[out=0,in=-90] (n/L);
              \path 
                coordinate[dot,label=below:$\strut \eta $] (unit) at (spath cs:guide1 0.5)
                coordinate[dot,label=above:$\strut \epsilon $] (counit) at (spath cs:guide2 0.5)
              ;
              \draw [spath/save=wire] (n/L) to[out=-90,in=0] (counit.center) to[out=180,in=0] (unit.center) to[out=180,in=90] (s/L);

              \begin {scope}[on background layer]
                \fill [catd] (nw) to (n/L) to[spath/use={wire,weld}] (s/L) to (sw) to (nw) to cycle;
                \fill [catc] (n/L) to[spath/use={wire,weld}] (s/L) to (se) to (ne) to cycle;
              \end {scope}
            \end {tikzpicture}
         $=$

      \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
        \CreateRect {3}{3}
        \path 
          coordinate[label=above:$\strut p$] (n) at (spath cs:north 0.5)
          coordinate[label=below:$\strut p$] (s) at (spath cs:south 0.5)
        ;
        \draw  (n) to (s);
        \node [between = nw and s, opacity=0.6] {$E$};
        \node [between = n and se, opacity=0.6] {$U$};
        \begin {scope}[on background layer]
          \fill [catd] (nw) rectangle (s);
          \fill [catc] (n) rectangle (se);
        \end {scope}
      \end {tikzpicture}









