            \draw [spath/save=wire] (n/q) to[out=-90,in=180] (counit.center) to[out=0,in=180] (unit.center) to[out=0,in=90] (s/q);
            \begin {scope}[on background layer]
              \fill [catc] (nw) to (n/q) to[spath/use={wire,weld}] (s/q) to (sw) to cycle;
              \fill [catd] (n/q) to[spath/use={wire,weld}] (s/q) to (se) to (ne) to cycle;
            \end {scope}
          \end {tikzpicture}
         $=$

      \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
        \CreateRect {3}{3}
        \path 
          coordinate[label=above:$\strut q$] (n) at (spath cs:north 0.5)
          coordinate[label=below:$\strut q$] (s) at (spath cs:south 0.5)
        ;
        \draw  (n) to (s);
        \node [between = nw and s, opacity=0.6] {$U$};
        \node [between = n and se, opacity=0.6] {$E$};
        \begin {scope}[on background layer]
          \fill [catc] (nw) rectangle (s);
          \fill [catd] (n) rectangle (se);
        \end {scope}
      \end {tikzpicture}

fr:resource.fr:resource-source:

