      \begin {tikzpicture}[scale=0.5,baseline=($(nw)!0.5!(se)$)]
        \CreateRect {3}{2}
        \path 
          coordinate[label=above:$\strut \Phi _{E}$] (n) at (spath cs:north 0.5)
          coordinate[label=below:$\strut \Psi _{E}$] (s) at (spath cs:south 0.5)
        ;
        \draw [spath/save=vert] (n) to (s);
        \path  coordinate[dot,label=left:$\alpha _{E}$] (cell) at (spath cs:vert 0.5);
        \begin {scope}[on background layer]
          \fill [catd] (nw) rectangle (s);
          \fill [cate] (n) rectangle (se);
