                coordinate[label=below:$\strut q$] (s/L) at (spath cs:south 0.2)
              ;
              \path [spath/save=guide1] (n/L) to[out=180,in=90] (s/L);
              \path [spath/save=guide2] (s/L) to[out=0,in=-90] (n/L);
              \path 
                coordinate[dot,label=below:$\strut \eta $] (unit) at (spath cs:guide1 0.5)
                coordinate[dot,label=above:$\strut \epsilon $] (counit) at (spath cs:guide2 0.5)
              ;
              \draw [spath/save=wire] (n/L) to[out=-90,in=0] (counit.center) to[out=180,in=0] (unit.center) to[out=180,in=90] (s/L);

              \begin {scope}[on background layer]
                \fill [catd] (nw) to (n/L) to[spath/use={wire,weld}] (s/L) to (sw) to (nw) to cycle;
                \fill [catc] (n/L) to[spath/use={wire,weld}] (s/L) to (se) to (ne) to cycle;
              \end {scope}
