\begin {aligned}
 e&\colon  \mathbb {N}\twoheadrightarrow  \mathbb {Z}\\
 e{\mathopen {}\left (i\right )\mathclose {}} &=
 \begin {cases}
  0 &\text {when } i=0\\
  i\div  2 &\text {when } i > 0 \land  2\mid  i\\
  -{\mathopen {}\left (i+1\right )\mathclose {}}\div  2 &\text {when } i > 0 \land  \lnot {\mathopen {}\left (2\mid  i\right )\mathclose {}}
 \end {cases}
\end {aligned}
