\begin {tikzpicture}[node distance=2cm,on grid]
  \node [state,initial] (q0) {$q_0$};
  \node [state,accepting,right=of q0] (q1) {$q_1$};
  \path [->]
    (q0) edge node[above] {$a$} (q1)
  ;
\end {tikzpicture}

