locale Magma { A => car : Type, car.isSet : isSet A·car, cmp : (x y : A·car) → A·car }
locale Magma.Hom { f => dom : Magma, cod : Magma, car : (x : f·dom·car) → f·cod·car, cmp : (x y : f·dom·car) → Id f·cod·car (f·car (f·dom·cmp x y)) (f·cod·cmp (f·car x) (f·car y)) }
locale Semigroup { A => splice magma : Magma, cmp.assoc : (x y z : A·car) → Id A·car (A·cmp (A·cmp x y) z) (A·cmp x (A·cmp y z)) }
locale Semigroup.Hom {f => dom : Semigroup, cod : Semigroup, splice magma.hom : Magma.Hom / {dom := f·dom·magma, cod := f·cod·magma} }
locale Monoid { A => splice semigroup : Semigroup, unit : A·car, cmp.leftUnit : (x : A·car) → Id A·car (A·cmp A·unit x) x, cmp.rightUnit : (x : A·car) → Id A·car (A·cmp x A·unit) x }
locale Monoid.Hom {f => dom : Monoid, cod : Monoid, splice semigroup.hom : Semigroup.Hom / {dom := f·dom·semigroup, cod := f·cod·semigroup}, unit : Id f·cod·car (f·car f·dom·unit) f·cod·unit }
locale Group { G => splice monoid : Monoid, inv : (x : G·car) → G·car, inv.inv : (x : G·car) → Id G·car (G·inv (G·inv x)) x }
locale AbelianGroup { A => splice group : Group, splice commutativeOperation : CommutativeOperation / {car := A·car, cmp := A·cmp} }
